import { loadGame, startBoard } from '../api/gameApi';
import {
  START_GAME_SUCCESS,
  RESTAR_GAME_SUCCESS,
  CELL_STATUS,
  GAME_STATUS_SUCCESS,
  GAME_STATUS,
  GAME_ID_STATUS,
  PUT_FLAG,
  UPDATE_GAME,
  FLIP_CELL,
  LOAD_BOARD_SUCCESS,
  NOTHING
} from '../constants/actionTypes';

//export function startGameBoardZZ() {
  //console.log("estoy en funcion startGameBoard dentro de /actions");
  //return (startGameSuccess(loadGame()));
//}

//export function startGameSuccess() {
export function startGameBoard() {
  console.log("Estoy en startGameBoard dentro de /actions")
  //console.log(props)
  var boardWithMines = loadGame()
  console.log(boardWithMines);
  console.log("listo")
  //return function(dispatch) {
    console.log("AAAAAAAAAAAAAAAAAAAAAAA")
    let data = {
      board: boardWithMines,
      game_status: 3,
      board_status: startBoard(),
      flipped_boxes: 0
    }
    fetch('/game/start', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      console.log("respuesta del controlador start_game:");
      console.log(data);
      //si recibo data.message == "Nuevo", continua el flujo normal
      //pero si recibo data.message == "Guardado"
      //debo utilizar la respuesta para pintar las banderas
      if (data.message == "Guardado") {
        $(`.prueba-componente`).css('background', '#980f8c');
      } else {
        if (data.message == "Nuevo") {
          $(`.prueba-componente`).css('background', '#054bd8');
          startGameExit(data);  
        }
      }
      //startGameExit(data);
    })
    .catch(error => console.log('error', error));
  //}
}

function startGameExit(data) {
  console.log("ULTIMA FUNCION EN EJECUCION")
  return {
    type: START_GAME_SUCCESS,
    trash: data
  };
}

export function restartGame() {
  console.log("Estamos en la funcion restartGame")
  var boardWithMines = loadGame()
  console.log(boardWithMines);
  console.log("listo")
  return function(dispatch) {
    console.log("despachamos la funcion que ejecutara el fetch")
    let data = {
      board: boardWithMines,
      game_status: 3,
      board_status: startBoard(),
      flipped_boxes: 0
    }
    fetch('/game/restart', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      console.log("respuesta del controlador reset_game:");
      console.log(data);
      dispatch(restartGameSuccess(data));
    })
    .catch(error => console.log('error', error));
  }
}

function restartGameSuccess(data) {
  return {
    type: RESTAR_GAME_SUCCESS,
    gameId: data.game_id.$oid,
    gameStatus: data.game_status
  }
}

export function switchFlag(coordinates, validateCellStatus, cell, props) {
  console.log("estamos en la funcion switchFlag");
  console.log(coordinates)
  console.log(validateCellStatus)
  console.log(cell)
  return function(dispatch) {
    let data = {
      coordinates: coordinates
    }
    fetch('/game/flag', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      validateCellStatus(cell, data, props)
      console.log("respuesta del estado de la celda:")
      console.log(data.cell_status);
      dispatch(flagSuccess(data.cell_status));
    })
    .catch(error => console.log('error', error));
  }
}

function flagSuccess(response) {
  console.log("se obtuvo el valor de la celda:");
  console.log(response);
  return {
    type: CELL_STATUS,
    cellStatus: response
  };
}

export function knowTheGameState(gameStatus) {
  if (gameStatus == 3) {
    console.log("Estamos en un juego nuevo, obtener el Id para ir guardando los cambios")
    return function(dispatch) {
      fetch('/game/get/id')
      .then(response => response.json())
      .then(data =>{
        console.log(data);
        dispatch(gameIdSuccess(data));
      });
    }

  } else {
    if (gameStatus == 0) {
      console.log("ya se estaba jugando, restaurar el tablero guardado")
    } else {
      if (gameStatus == 1) {
        console.log("El jugador ha perdido")
      } else {
        if (gameStatus == 2) {
          console.log("El jugador ha ganado")
        }
      }
    }
  }
  return {
    type: NOTHING
  }
}

function gameIdSuccess(data) {
  console.log("llegamos a GAMEIDSUCCESS")
  return {
    type: GAME_ID_STATUS,
    gameId: data.game_id.$oid,
    gameStatus: data.game_status
  }
}

export function getStatusGame (props) {
  console.log("Estamos en la funcion getStatusGame")
  console.log(props)
  return {
    type: GAME_STATUS_SUCCESS,
    data: props   //AUN NO SE HA ESCRITO LA RESPUESTA EN STATES.JS
  }
}

export function markTheBoard (gameId, coordinates) {
  console.log("llegamos a markTheBoard")
  return function(dispatch) {
    let data = {
      gameId: gameId,
      coordinates: coordinates
    }
    fetch('/game/push/flag', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      console.log("respuesta del estado de la celda:")
      console.log(data)
      console.log(data.game_status);
      dispatch(markBoardSuccess(data.game_status));
    })
    .catch(error => console.log('error', error));
  }
}

function markBoardSuccess (gameStatus) {
  console.log("llegamos a markBoardSuccess")
  return {
    type: PUT_FLAG,
    gameStatus: gameStatus
  }
}
