class Mines extends Component {
  constructor(props) {
    console.log("estamos en el constructor de Mines");
    super(props);
    this.handleRestart = this.handleRestart.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleFlag = this.handleFlag.bind(this);
    this.handleFlip = this.handleFlip.bind(this);
    this.buttonDetect = this.buttonDetect.bind(this);
  }
  componentDidMount() {
    console.log("Estamos en componentDidMount de Mines");
    console.log(this.props);
    this.props.startTheGame(this.props.gameStatus)
    this.props.verifyTheStatusGame(this.props)
  }

  handleRestart() {
    this.props.restartGame();
  }
  handleChange() {
    this.props.updateGame();
  }
  handleFlag() {
    this.props.setFlag();
  }
  handleFlip() {
    this.props.flipCell();
  }

  buttonDetect(event){
    if (event.button==2) { //Boton derecho para colocar o quitar bandera
      //codigo
      //var flag = 1;
      //verificar el valor que tiene la celda para saber si se coloca
      //o se retira la bandera
      console.log("Se ha presionado el click DERECHO!!");
