  const gameState = {
    data: [],
    flippedBoxes: {quantity: 0},
    gameStatus: {status: 3},
    cellStatus: {status: 0}
  };

  function gameReducer(state = gameState, action) {
    console.log("llegamos a la funcion switch de /reducers/states");
