import { loadGame } from '../api/gameApi';
import {
  START_GAME_SUCCESS,
  RESET_GAME,
  UPDATE_GAME,
  PUSH_FLAG,
  FLIP_CELL,
} from '../constants/actionTypes';

export function startGameBoard() {
  console.log("estoy en funcion startGameBoard dentro de /actions");
  return function(dispatch) {
    return dispatch(startGameSuccess(loadGame()));
  };
}

function startGameSuccess(boardWithMines) {
  console.log("Estoy en startGameSuccess dentro de /actions")
  console.log(boardWithMines);
  console.log("listo")
  const data = new FormData();
  data.append('board', boardWithMines);
  fetch('/game/start', {
    method: 'POST',
    body: data
  })
  .then(response => response.json())
  .then(data => console.log(data))
  .catch(error => console.log('error', error));

  return {
    type: START_GAME_SUCCESS,
    boardWithMines
  };
}
