import { loadGame, startBoard } from '../api/gameApi';
import {
  START_GAME_SUCCESS,
  RESET_GAME,
  CELL_STATUS,
  GAME_STATUS,
  UPDATE_GAME,
  FLIP_CELL,
  LOAD_BOARD_SUCCESS
} from '../constants/actionTypes';

//export function startGameBoardZZ() {
  //console.log("estoy en funcion startGameBoard dentro de /actions");
  //return (startGameSuccess(loadGame()));
//}

//export function startGameSuccess() {
export function startGameBoard() {
  console.log("Estoy en startGameBoard dentro de /actions")
  //console.log(props)
  var boardWithMines = loadGame()
  console.log(boardWithMines);
  console.log("listo")
  //return function(dispatch) {
    console.log("AAAAAAAAAAAAAAAAAAAAAAA")
    let data = {
      board: boardWithMines,
      game_status: 3,
      board_status: startBoard(),
      flipped_boxes: 0
    }
    fetch('/game/start', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      console.log("respuesta del controlador con el ID:");
      console.log(data);
      console.log(data.game_id);
      console.log(data.game_id.$oid);
      startGameExit(data);
    })
    .catch(error => console.log('error', error));
  //}
}

function startGameExit(data) {
  console.log("ULTIMA FUNCION EN EJECUCION")
  return {
    type: START_GAME_SUCCESS,
    gameId: data
  };
}

export function restartGame() {
  startGameBoard();
}

export function switchFlag(coordinates, myFunction, celda) {
  console.log("estamos en la funcion switchFlag");
  console.log(coordinates)
  console.log(myFunction)
  console.log(celda)
  return function(dispatch) {
    let data = {
      coordinates: coordinates
    }
    fetch('/game/flag', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json' }
    })
    .then(response => response.json())
    .then(data => {
      myFunction(celda, data)
      console.log("respuesta del estado de la celda:")
      console.log(data.cell_status);
      dispatch(flagSuccess(data.cell_status));
    })
    .catch(error => console.log('error', error));
  }
}

function flagSuccess(response) {
  console.log("se obtuvo el valor de la celda:");
  console.log(response);
  return {
    type: CELL_STATUS,
    cellStatus: response
  };
}

export function knowTheGameState(gameStatus) {
  if (gameStatus == 3) {
    console.log("Estamos en un juego nuevo, obtener el Id para ir guardando los cambios")
    fetch('/game/get/id')
    .then(response => {
      //return response.json();
      console.log(response)
    });

  } else {
    if (gameStatus == 0) {
      console.log("ya se estaba jugando, restaurar el tablero guardado")
    } else {
      if (gameStatus == 1) {
        console.log("El jugador ha perdido")
      } else {
        if (gameStatus == 2) {
          console.log("El jugador ha ganado")
        }
      }
    }
  }
  return {
    type: GAME_STATUS,
    gameStatus: gameStatus
  }
}

export function markTheBoard () {
  fetch('/game/push/flag', {
    method: 'POST',
    body: JSON.stringify(data),
    headers: {'Content-Type': 'application/json' }
  })
  .then(response => response.json())
  .then(data => {
    myFunction(celda, data)
    console.log("respuesta del estado de la celda:")
    console.log(data.cell_status);
    dispatch(flagSuccess(data.cell_status));
  })
  .catch(error => console.log('error', error));
}
