import React, { Component } from 'react';
import {connect} from 'react-redux';
import Styled from 'styled-components';
import {restartGame, switchFlag, knowTheGameState, markTheBoard} from '../../src/actions/actions'
import customFlag from '../../../assets/images/flag';
import fire from '../../../assets/images/fire';
import bombs from '../../../assets/images/bomb';
import one from '../../../assets/images/one';
import two from '../../../assets/images/two';
import three from '../../../assets/images/three';
import four from '../../../assets/images/four';
import five from '../../../assets/images/five';
import six from '../../../assets/images/six';
import seven from '../../../assets/images/seven';
import eight from '../../../assets/images/eight';

class Mines extends Component {
  constructor(props) {
    console.log("estamos en el constructor de Mines");
    super(props);
    this.handleRestart = this.handleRestart.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleFlag = this.handleFlag.bind(this);
    this.handleFlip = this.handleFlip.bind(this);
    this.elementSelected = this.elementSelected.bind(this);
    this.buttonDetect = this.buttonDetect.bind(this);
  }
  componentDidMount() {
    console.log("Estamos en componentDidMount de Mines");
    console.log(this.props);
    this.props.startTheGame(this.props.gameStatus)
  }
  handleRestart() {
    this.props.restartGame();
  }
  handleChange() {
    this.props.updateGame();
  }
  handleFlag() {
    this.props.setFlag();
  }
  handleFlip() {
    this.props.flipCell();
  }

  buttonDetect(event){
    if (event.button==2) { //Boton derecho para colocar o quitar bandera
      //codigo
      var flag = 1;
      this.elementSelected(event, flag);
    } else {
      if (event.button == 0) { //Boton izquierdo para descubrir la casilla
        var flag = 0;
        this.elementSelected(event, flag);
      }
    }
  }
  validateCellStatus(cell, data, props){
    console.log("llegamos a validateCellStatus")
    console.log(cell)
    console.log(data.cell_status)
    //En data.cell_status se encuentra el valor de la celda, obtenido desde la
    //base de datos, del tablero board_status, que contiene los valores de cada celda
    //de este modo puedo saber si debo colocar una bandera o retirarla, siendo las
    //unicas opciones para el caso de presionar el click derecho
    if (data.cell_status == 0) {
      console.log("estamos listos para colocar una bandera");
      var cellPosition = cell.split(" ");
      console.log(cellPosition);
      console.log(cellPosition[0]);
      console.log(cellPosition[1]);
      var image = new Image();
      image.src= customFlag
      $(`.${cellPosition[0]}.${cellPosition[1]}`).append(image)
      $(`.${cellPosition[0]}.${cellPosition[1]}`).css('background', '#1636ad');
      var coordinates = cellPosition[1].split("");
      console.log(coordinates)
      console.log("verificando acceso a las props")
      console.log(props)
      //Despues de poner la bandera hay que marcar el tablero board_status que contiene los estados de celda:
      //y que se encuentra en la base de datos, para ello se envia el ID del tablero de juego actual
      this.props.setFlag(props.gameId, coordinates)
    }
  }
  elementSelected(event, flag){
    if (event.srcElement) {
      var tag = event.srcElement.tagName;
      console.log("otros navegadores")
      //ELIMINAR ESTA OPCION
    }
    else {
      if (event.target) {
        if (flag == 1) {
          //verificar el valor que tiene la celda para saber si se coloca
          //o se retira la bandera
          console.log("Se ha presionado el click DERECHO!!");
          document.oncontextmenu = function(){return false}
          if (event.target.tagName == "IMG") {
            var cell = event.target.parentElement.className;
            console.log(`nombre de la clase padre: ${cell}`);
          } else {
            var cell = event.target.className;
            console.log(`nombre de la clase ${cell}`);
          }
          var position = cell.split(" ")[1];
          var coordinates = position.split("");
          this.props.statusFlag(coordinates, this.validateCellStatus, cell, this.props);
        } else {
          console.log("Se ha presionado el click IZQUIERDO!!")
          console.log(event.target.className)
          var cell = event.target.className
          var position = cell.split(" ")[1]
          //this.props.seeBoard()
        }
      }
    }
  }

  render() {
    console.log("Estamos en el render de Mines")
    const Button = Styled.button`
      cursor: pointer;
      background: transparent;
      font-size: 18px;
      border-radius: 8px;
      color: #0023ff;
      border: 2px solid #0023ff;
      margin: 1px;
      padding: 8px;
      transition: 0.5s all ease-out;
      &:hover {
        background-color: #1cd1d1;
        color: white;
      }
    `;
    var table = document.createElement("table");
    table.cellSpacing = "10";
    var tblBody = document.createElement("tbody");
    for (var row = 0; row < 10; row++) {
      var rows = document.createElement("tr");
      for (var column = 0; column < 10; column++) {
        var columns = document.createElement("td");
        //var image = document.createElement("img");
        //image.src="packs/media/images/bomb.png";
        columns.className = `cell ${row}${column}`
        //columns.appendChild(image);
        rows.appendChild(columns);
      };
      tblBody.appendChild(rows);
    };
    table.appendChild(tblBody);
    console.log("Vamos a entrar al return del render de Mines")
    return (
      <div className="game-container">
        <div className="header">
          <div className="title"></div>
        </div>
        <div className="restart-button">
          <Button onClick={this.handleRestart} className="btn btn-secondary">
            Reset Game
          </Button>
        </div>
        <div className="game-background">
          <div className="help-container">
            <p>Estado celda clickeada: {this.props.cellStatus}</p>
            <p>Estado del juego: {this.props.gameStatus}</p>
            <p>GameID: {this.props.gameId}</p>
            <p>valor de bandera: {this.props.cellValue}</p>
          </div>
          <div className="game-board-container" onMouseDown={this.buttonDetect}
            dangerouslySetInnerHTML={{ __html: table.outerHTML }}>
          </div>
        </div>
        <div className="Prueba">

        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  console.log("estamos en mapStateToProps Mines");
  return {
    cellStatus: state.gameReducer.cellStatus.status,
    flippedBoxes: state.gameReducer.flippedBoxes,
    gameStatus: state.gameReducer.gameStatus.status,
    gameId: state.gameReducer.gameId,
    cellValue: state.gameReducer.cellValue
  };
}

function mapDispatchToProps(dispatch) {
  console.log("estamos en mapDispatchToProps Mines");
  return {
    startTheGame: (gameStatus) => dispatch(knowTheGameState(gameStatus)),
    restartGame: () => dispatch(restartGame()),
    statusFlag: (coordinates, validateCellStatus, cell, props) => dispatch(switchFlag(coordinates, validateCellStatus, cell, props)),
    setFlag: (gameId, coordinates) => dispatch(markTheBoard(gameId, coordinates))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Mines);
